import React, { useState } from "react";
import Tasks from "./Tasks";
import EditNoteIcon from "@mui/icons-material/EditNote";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import { useDispatch } from "react-redux";
import { editCardName, deleteCard } from "../redux/todoSlice";

const Card = ({ title, todoList, groupId }) => {
  const [modal, setModal] = useState(false);
  const [newTitle, setNewTitle] = useState(title);

  const dispatch = useDispatch();

  const changeCardTitle = () => {
    dispatch(editCardName({ cardId: groupId, title: newTitle }));
    setModal(false);
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <div className="flex flex-col border bg-white shadow-lg p-4 rounded-lg transition-transform transform hover:scale-105">
        <div className="flex items-center justify-between mb-4">
          <h2 className="font-semibold text-lg text-gray-800">{title}</h2>
          <div className="space-x-2">
            <EditNoteIcon
              titleAccess="Edit card title"
              onClick={() => setModal(true)}
              className="text-green-500 cursor-pointer hover:text-green-600"
            />
            <DeleteForeverIcon
              onClick={() => dispatch(deleteCard({ cardId: groupId }))}
              className="text-red-500 cursor-pointer hover:text-red-600"
              titleAccess="Delete Card"
            />
          </div>
        </div>

        {modal && (
          <div className="mb-4">
            <p className="mb-2 text-gray-600">Enter new Card title here:</p>
            <div className="flex items-center space-x-2">
              <input
                type="text"
                value={newTitle}
                placeholder="New Card title"
                className="px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 flex-1"
                onChange={(e) => setNewTitle(e.target.value)}
              />
              <button
                onClick={changeCardTitle}
                className="px-3 py-2 bg-blue-500 text-white rounded-md shadow-sm hover:bg-blue-600"
              >
                Submit
              </button>
            </div>
          </div>
        )}

        <Tasks todoList={todoList} />
      </div>
    </div>
  );
};

export default Card;
