import React, { useState } from "react";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";
import EditNoteIcon from "@mui/icons-material/EditNote";
import { Box, Modal, Typography } from "@mui/material";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "white",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

const Task = ({
  taskTitle,
  taskDate,
  taskId,
  handleDeleteTask,
  handleEditTask,
  openModel,
  setOpenModel,
}) => {
  const [editText, setEditText] = useState(taskTitle);

  return (
    <div className="border bg-white shadow-md p-3 rounded-lg flex flex-col gap-3 my-2">
      <div className="flex justify-between items-start">
        <div>
          <h4 className="text-lg font-medium text-gray-800">{taskTitle}</h4>
          <p className="text-sm text-gray-600">
            <span className="font-medium">Date: </span>
            {taskDate}
          </p>
        </div>
        <div className="flex space-x-2">
          <button onClick={() => setOpenModel(true)}>
            <EditNoteIcon
              className="text-green-500 hover:text-green-600"
              titleAccess="Edit Task"
            />
          </button>
          <button onClick={() => handleDeleteTask(taskId)}>
            <DeleteForeverIcon
              className="text-red-500 hover:text-red-600"
              titleAccess="Delete Task"
            />
          </button>
        </div>
      </div>

      <Modal
        open={openModel}
        onClose={() => setOpenModel(false)}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography variant="h6" component="h2">
            Edit Text
          </Typography>
          <Typography sx={{ mt: 2 }}>
            <textarea
              onChange={(e) => setEditText(e.target.value)}
              value={editText}
              className="border-2 w-full rounded-md px-2 py-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
              rows={4}
            >
              {taskTitle}
            </textarea>
          </Typography>
          <button
            onClick={() => handleEditTask(taskId, editText)}
            className="w-full mt-4 px-3 py-2 bg-blue-500 text-white rounded-md shadow-sm hover:bg-blue-600"
          >
            Submit
          </button>
        </Box>
      </Modal>
    </div>
  );
};

export default Task;
