import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Cards from "../components/Cards";
import { useDispatch, useSelector } from "react-redux";
import { newBoardDispatch, addCard } from "../redux/todoSlice";
import toast, { Toaster } from "react-hot-toast";

const Home = () => {
  const dispatch = useDispatch();
  const states = useSelector((card) => card.todo);
  const [stateToMap, setStateToMap] = useState(
    states?.currState === "mainCard" ? states.mainCard : states.newBoard
  );

  const changeState = () => {
    dispatch(newBoardDispatch());
    setStateToMap(
      states.currState === "mainCard" ? states.newBoard : states.mainCard
    );
  };

  const [addCardTitle, setAddCardTitle] = useState("");

  const handleAddCard = () => {
    if (addCardTitle === "") {
      toast.error("Add a Card Title");
    } else {
      dispatch(addCard({ title: addCardTitle }));
      setAddCardTitle("");
    }
  };

  useEffect(() => {
    setStateToMap(
      states.currState === "mainCard" ? states.mainCard : states.newBoard
    );
  }, [states]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-cyan-500 to-blue-100 px-10 py-5">
      <Navbar changeState={changeState} />
      <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4">
        <Cards stateToMap={stateToMap} />
      </div>
      <div className="bg-gray-100 p-4 my-4 flex items-center gap-4 rounded-md shadow-lg">
        <h2 className="text-lg font-medium text-gray-800">Add Card</h2>
        <input
          value={addCardTitle}
          onChange={(e) => setAddCardTitle(e.target.value)}
          type="text"
          className="flex-1 px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Add a new card title"
        />
        <button
          onClick={handleAddCard}
          className="px-4 py-2 bg-blue-500 text-white rounded-md shadow hover:bg-blue-600"
        >
          Submit
        </button>
      </div>
      <Toaster />
    </div>
  );
};

export default Home;
